import React from "react";
import '../App.css'
import { Button } from "./Button";
import './HeroSection.css'

function HeroSection() {
    return (
        <div className='hero-container'>
            <video src="https://raw.githubusercontent.com/ricvergara/website-product/gh-pages/videos/video-2.mp4" autoPlay loop muted/>
            <h1>ADVENTURES AWAITS</h1>
            <p>What are you waiting for?</p>

            <div className="hero-btns">
                <Button className='btns' buttonStyle='btn--primary' buttonSize='btn--large'>GET STAaaaaaaaaaaaaaaaaaaaaRTED</Button>
            </div>
            <div className="hero-btns">
                <Button className='btns' buttonStyle='btn--outline' buttonSize='btn--large'>WATCH TRAILER<i className='far-fa-play-circle'/></Button>
            </div>


        </div>
    )
}

export default HeroSection;
